      <body id="json">  
          
          <h1> JSON FORMAT DATA FILE FROM REGISTERED USER'S TABLE </h1>
           <?php   
           $connect = mysqli_connect("localhost", "root", "", "db_guviintern");  
           $sql = "SELECT * FROM registered_user";  
           $result = mysqli_query($connect, $sql);  
           $json_array = array();  
           while($row = mysqli_fetch_assoc($result))  
           {  
                $json_array[] = $row;  
           }  
           /*echo '<pre>';  
           print_r(json_encode($json_array));  
           echo '</pre>';*/  
           echo json_encode($json_array);  
